
public class AreaTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double radius, area, area2;
		radius = 5.0;
		area = 3.141592 * radius * radius; // 원넗이
		area2 = 3.141592 * radius * 2; // 원 둘레
		System.out.println("원의 면적은 : " + area);
		System.out.println("원의 둘레은 : " + area2);

	}

}
