
public class Light {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long lightspeed;	//  빛의 속도 
		long distance; 	   // 빛이 1년 동안 가는속도
		
		lightspeed = 300000;
		distance = lightspeed * 365L * 24 * 60 * 60;
		
		System.out.println("빛이 1년 동안 가는 거리 : " + distance + "km.");
		
		
	}

}
